		if (score > 0) {
			[ result addObject: word ];
			word = @"";
			p = @"B";
		}
		p1 = p2;
		p2 = p3;
		p3 = p;
		word = [ word stringByAppendingString: [ seg objectAtIndex: i ] ];
	}
	[ result addObject: word ];
	
	return result;
}


@end
